mapper.select:

select
id, entname, entlocation, entbusiness, entperson, enttype, interviewstudent, interviewaspect,
studentclass, entwhite, remark, GMT_CREATE, GMT_MODIFIED
from student_ent_info
where 	1=1

  and entname like concat('%', #{entName}, '%')


  and GMT_CREATE >= #{gmtCreateBegin}


  and GMT_CREATE <= #{gmtCreateEnd}


limit ${pageOffset},${pageSize}

select count(*)   from student_ent_info
where
1=1

  and entname like concat('%', #{entName}, '%')


  and GMT_CREATE >= #{gmtCreateBegin}


  and GMT_CREATE <= #{gmtCreateEnd}
